import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 50px;

    .formulario {
        background-color: #8B0000;
        color: #FFFAFA;
        display: flex;
        flex-direction: column;
        width: 450px;
        height: auto;
        padding: 10px;
        border-radius: 5px;

        .titulo-form {
            text-align: center;
            font-size: 30px;
            padding-bottom: 15px;
        }
    }
`;
