import styled from 'styled-components';

export const Container = styled.div`

    .titulo {
        font-size: 20px;
        color: black;
        font-style: normal;
        padding-left: 5px;
        border-left: solid;
        border-left-color: #8B0000;
        margin-bottom: 5px;
    }

    .sub-titulo {
        margin-bottom: 8px;
        background-color: #8B0000;
        color: #FFFAFA;
        padding-left: 5px;
        border-radius: 5px;
        padding-bottom: 3px;
        padding-top: 3px;
        width: 240px;
    }

    table {
        border-style: solid;
        width: 300px;
        height: 200px;
        padding-left: 5px;

        .titulo-tabela {
            text-align: left;
            border-bottom-style: solid;
        }
    }
`;
