import styled from 'styled-components';

export const Container = styled.div`
    display: flex;

    .lado-esquerdo {
        width: 50%;
        height: 500px;
        background-color: green;
    }
    .lado-direito {
        width: 50%;
        height: 500px;
        background-color: blue;
    
        .imagem {
            height: 450px;
            width: 100%;
        }

        .texto-imagem{
            display: flex;
            justify-content: center;
            align-items: center;
        }
    }
`;
